var matchM = function (originInput, formatData) {
 	var re = new RegExp(originInput, "g");
	return{
		searchM: function(chunk){
			var num = 0;
			var index = chunk.search(re);
			while(index < 0){
				for(var key in formatData){
					console.log("i", key);
						console.log("f", formatData[key]);
					if (formatData[key].chaifen) {
						var newRe = new RegExp(originInput.replace(key, formatData[key].chaifen), "g");
						index = chunk.search(newRe);
					}
				}
				num ++;
				if(num > originInput.length){
					break;
				}
			};
			return index;
		}
	}
}
exports.matchM = matchM;